	.con div {
		overflow: hidden;
		height: 400px;
		min-width: 300px;
		margin-left: 50px;
		animation: slide 50s linear reverse infinite;
	}

	.con div img,
	.con1 div img {
		width: 100%;
		height: 100%;
		object-fit: cover;
	}

	@keyframes slide {
		0% {
			transform: translate3d(0, 0, 0);
		}
		100% {
			transform: translate3d(-3000px, 0, 0); /* The image width */
		}
	}
</style>
